mapper.select:
select count(*)
from Follow
where followerId = #{followerId}

select count(*)
from Follow
where followingId = #{followingId}

mapper.delete:
   		delete
   		from Follow
		where followerId=#{followerId} and followingId=#{followingId}

mapper.select:
   		select count(*)
   		from Follow
		where followerId=#{followerId} and followingId=#{followingId}
