mapper.select:
select count(*)
from FOLLOW
where followerId = #{followerId}

select followingId 
from FOLLOW
where followerId = #{followerId}

select followerId 
from FOLLOW
where followingId = #{followingId}

select count(*)
from FOLLOW
where followingId = #{followingId}

mapper.delete:
   		delete 
   		from FOLLOW
		where followerId=#{followerId} and followingId=#{followingId}

mapper.select:
   		select count(*)
   		from FOLLOW
		where followerId = #{followerId} and followingId = #{followingId}
